void draw_bitmap(int x, int y,unsigned char bitmap[], int sx, int sy) {
	int bit;
	int data,cy,cx;

	for (cy =0; cy<sy; cy++){
		bit = cy % 8;
		for (cx =0; cx<sx; cx++) {
			data = bitmap[cx + (cy/8)*sx];
			if( (data & (1<< bit) )  > 0) {
				lcd_draw_pixel(x +cx ,y+cy, Pixel_Set);
			}
			else {
				lcd_draw_pixel(x +cx ,y+cy, Pixel_Clear);
			}
		}
	}
}

